package com.example.app4;

import androidx.appcompat.app.AppCompatActivity;
import androidx.constraintlayout.widget.ConstraintLayout;

import android.os.Bundle;
import android.view.View;
import android.widget.ImageView;
import android.widget.RadioButton;
import android.widget.Toast;

import com.bumptech.glide.Glide;

public class MainActivity extends AppCompatActivity {

    ImageView img;
    RadioButton r1,r2,r3,r4;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        r1 =findViewById(R.id.rbayi);
        r2=findViewById(R.id.rbkus);
        r3=findViewById(R.id.rbkedi);
        r4=findViewById(R.id.rbkopek);
        img=findViewById(R.id.imageView2);
    }

    public void select(View view) {
        RadioButton rb = (RadioButton) view;

        if(((RadioButton)view).getId()==R.id.rbayi)
        {
            Toast.makeText(this, rb.getText().toString(), Toast.LENGTH_SHORT).show();
            //back.setBackgroundColor(Color.YELLOW);
            Glide.with(this).load("data:image/jpeg;base64,").into(img);

        }
        if(((RadioButton)view).getId()==R.id.rbkus)
        {
            Toast.makeText(this, rb.getText().toString(), Toast.LENGTH_SHORT).show();
           // back.setBackgroundColor(Color.GREEN);
            Glide.with(this).load("data:image/jpeg;base64,").into(img);
        }
        if(((RadioButton)view).getId()==R.id.rbkedi)
        {
            Toast.makeText(this, rb.getText().toString(), Toast.LENGTH_SHORT).show();
            Glide.with(this).load("data:image/jpeg;base64,").into(img);
        }
        if(((RadioButton)view).getId()==R.id.rbkopek)
        {
            Toast.makeText(this, rb.getText().toString(), Toast.LENGTH_SHORT).show();
            //back.setBackgroundColor(Color.RED);
            Glide.with(this)
                    .load("https://petokulu.com/wp-content/uploads/2022/01/buyumeyen-kopek-cinsleri-1.jpg")
                    .into(img);
        }
    }
}